	    return c[1].substr(0,c[1].indexOf("&"));
	   }
	}														
}								 			
function trim(str)
{
	return str.replace(/(^\s*)|(\s*$)/g,"");
}

function isInteger( str ){
	var regu = /^[-]{0,1}[0-9]{1,}$/;
	return regu.test(str);
}

